mapper.select:
select
  user_id, user_name, user_password, version, user_nickname, id_deleted, gmt_create, gmt_update
from kk_blog.admin_user


        and user_id = #{userId}


        and user_name = #{userName}


        and user_password = #{userPassword}


        and version = #{version}


        and user_nickname = #{userNickname}


        and id_deleted = #{idDeleted}


        and gmt_create = #{gmtCreate}


        and gmt_update = #{gmtUpdate}
